CREATE OR REPLACE PROCEDURE baixar_prescricao_barras ( nr_prescricao_p bigint, nm_usuario_p text ) AS $body$
BEGIN

/** Baixar a prescricao através do código de barras */

update prescr_material
set    cd_motivo_baixa = 1,
       dt_baixa        = clock_timestamp(),
       nm_usuario      = nm_usuario,
       dt_atualizacao  = clock_timestamp()
where  nr_prescricao   = nr_prescricao_p;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
