CREATE OR REPLACE PROCEDURE baixar_prescricao_lote ( nr_prescricao_p bigint, nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_gerados_w		bigint;
qt_prescricao_w		integer;
nr_sequencia_w		integer;
cd_tipo_baixa_w		smallint;

c01 CURSOR FOR
	SELECT 	nr_sequencia
	from	prescr_material
	where 	nr_prescricao = nr_prescricao_p
	and 	coalesce(dt_baixa::text, '') = '';


BEGIN

select 	count(*)
into STRICT	qt_gerados_w
from 	ap_lote
where 	coalesce(ie_status_lote, 'G') = 'G'
and 	nr_prescricao = nr_prescricao_p;

select	count(*)
into STRICT	qt_prescricao_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

if (qt_gerados_w = 0) and (qt_prescricao_w > 0) then

	select 	max(cd_tipo_baixa)
	into STRICT	cd_tipo_baixa_w
	from 	ap_lote
	where 	nr_sequencia = nr_seq_lote_p;

	open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		update 	prescr_material
		set 	cd_motivo_baixa = cd_tipo_baixa_w,
			dt_baixa	= clock_timestamp()
		where 	nr_prescricao =  nr_prescricao_p
		and 	nr_sequencia = nr_sequencia_w;

		end;
	end loop;
	close c01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
