CREATE OR REPLACE PROCEDURE baixar_prescr_pendente ( qt_hora_adic_mat_p bigint, qt_hora_adic_proc_p bigint, cd_motivo_baixa_p bigint, dt_parametro_p timestamp) AS $body$
BEGIN

update	prescr_procedimento a
set	cd_motivo_baixa 	= cd_motivo_baixa_p,
	ie_status_execucao 	= 'BE'
where	cd_motivo_baixa = 0
and	coalesce(ie_status_atend,1) < 35
and	coalesce(dt_cancelamento::text, '') = ''
and	exists
	(SELECT	1
	from	Procedimento c,
		prescr_medica b
	where	a.nr_prescricao	= b.nr_prescricao
	and	(a.dt_prev_execucao + (coalesce(c.qt_hora_baixar_prescr,qt_hora_adic_proc_p) / 24) < dt_parametro_p)
	and	(b.dt_prescricao + ((b.nr_horas_validade + coalesce(c.qt_hora_baixar_prescr,qt_hora_adic_proc_p)) /24) < dt_parametro_p)
	and	c.cd_procedimento = a.cd_procedimento
	and	c.ie_origem_proced = a.ie_origem_proced)
and not exists (select	1
	from	procedimento_paciente x
	where	x.nr_prescricao = a.nr_prescricao
	and	x.nr_sequencia_prescricao = a.nr_sequencia);
commit;

update	prescr_material a
set	cd_motivo_baixa = cd_motivo_baixa_p
where	cd_motivo_baixa = 0
and exists
	(SELECT	1
	from 	prescr_medica b
	where	a.nr_prescricao = b.nr_prescricao
	and	(b.dt_prescricao + (qt_hora_adic_mat_p/24)) < dt_parametro_p)
and not exists (select	1
	from	conclusao_recom_apae w
	where	w.nr_prescricao = a.nr_prescricao);
/*OS95251 Identificado que não baixava os material quando atendido no ADEP antes da Farmácia*/

/*and	not exists(	select	1
			from	prescr_mat_hor c
			where	c.nr_prescricao		= a.nr_prescricao
			and	c.nr_seq_material	= a.nr_sequencia
			and	c.dt_fim_horario is not null);*/
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baixar_prescr_pendente ( qt_hora_adic_mat_p bigint, qt_hora_adic_proc_p bigint, cd_motivo_baixa_p bigint, dt_parametro_p timestamp) FROM PUBLIC;

