CREATE OR REPLACE PROCEDURE baixar_prescr_pend_acm_sn ( qt_hora_adicional_p bigint, cd_motivo_baixa_p bigint, dt_parametro_p timestamp) AS $body$
BEGIN

update	prescr_material a
set	dt_baixa = clock_timestamp(),
	cd_motivo_baixa = cd_motivo_baixa_p
where	cd_motivo_baixa = 0
and	((coalesce(ie_se_necessario,'N') = 'S') or (coalesce(ie_acm,'N') = 'S'))
and	exists (	SELECT	1
			from	prescr_medica b
		     	where	a.nr_prescricao = b.nr_prescricao
			  and (b.dt_prescricao + 1 + qt_hora_adicional_p/24) < dt_parametro_p)
and	not exists (	select	1 from cirurgia w
			where	w.nr_prescricao = a.nr_prescricao)
and	not exists (	select	1
			from	prescr_mat_hor c
			where	c.nr_prescricao		= a.nr_prescricao
			and	c.nr_seq_material	= a.nr_sequencia
			and	(c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> '')
			and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S');
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
