CREATE OR REPLACE PROCEDURE baixar_prescr_pend_cirurgica ( qt_dias_cirurgia_p bigint, cd_motivo_baixa_p bigint, dt_parametro_p timestamp) AS $body$
BEGIN

update	prescr_procedimento a
set	cd_motivo_baixa = cd_motivo_baixa_p
where	cd_motivo_baixa = 0
and	exists (SELECT	1
	from	cirurgia d
	where	a.nr_prescricao	= d.nr_prescricao
	and (d.dt_inicio_real - qt_dias_cirurgia_p < dt_parametro_p))
and not exists (select	1
	from	procedimento_paciente x
	where	x.nr_prescricao = a.nr_prescricao
	and	x.nr_sequencia_prescricao = a.nr_sequencia);
commit;

update	prescr_material a
set	cd_motivo_baixa = cd_motivo_baixa_p
where	cd_motivo_baixa = 0
and exists (SELECT	1
	from 	cirurgia d
	where	a.nr_prescricao	= d.nr_prescricao
	and	(d.dt_termino IS NOT NULL AND d.dt_termino::text <> '')
	and (d.dt_inicio_real - qt_dias_cirurgia_p) < dt_parametro_p)
and not exists (select 1
	from	material_atend_paciente x
	where	x.nr_prescricao	= a.nr_prescricao
	and	x.nr_sequencia_prescricao = a.nr_sequencia);
/*OS95251 Identificado que não baixava os material quando atendido no ADEP antes da Farmácia*/

/*and not exists(
	select	1
	from	prescr_mat_hor c
	where	c.nr_prescricao		= a.nr_prescricao
	and	c.nr_seq_material	= a.nr_sequencia
	and	c.dt_fim_horario is not null);*/
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
