CREATE OR REPLACE PROCEDURE baixar_procedimento_ge ( ie_status_execucao_p text, nr_seq_propaci_p bigint, nr_prescricao_p bigint, nr_seq_procedimento_p bigint, nr_interno_conta_p bigint, ie_recalcular_conta_p text, cd_motivo_baixa_p bigint, ie_atualizar_adep_p text, nr_atendimento_p bigint, ie_informa_justif_p text, nr_seq_motivo_just_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_cancela_w varchar(1) := 'N';
ie_considera_baixa_conta_w varchar(1) := 'N';

--Usado na Gestap de exames (Swing)		 
BEGIN 
 
ie_considera_baixa_conta_w := coalesce(obter_valor_param_usuario(942,360, 
	obter_perfil_ativo,nm_usuario_p,obter_estabelecimento_ativo),'N');
	 
 
if (ie_status_execucao_p = '20') or 
	(((ie_status_execucao_p = '10') or (ie_status_execucao_p = '15')) and 
	  ie_considera_baixa_conta_w = 'S') then 
	  
	if (nr_seq_propaci_p IS NOT NULL AND nr_seq_propaci_p::text <> '') then 
	 
		delete 
		from	procedimento_paciente 
		where	nr_sequencia = nr_seq_propaci_p;
	else 
		delete 
		from	procedimento_paciente 
		where	nr_prescricao = nr_prescricao_p 
		and	nr_sequencia_prescricao = nr_seq_procedimento_p;	
	end if;
end if;
 
if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') and (ie_recalcular_conta_p = 'S') then 
	begin 
	CALL recalcular_conta_paciente(nr_interno_conta_p,nm_usuario_p);
	end;
end if;
 
update 	prescr_procedimento 
set 	cd_motivo_baixa = cd_motivo_baixa_p, 
	dt_baixa = clock_timestamp(), 
	nm_usuario = nm_usuario_p, 
	dt_atualizacao = clock_timestamp() , 
	ie_status_execucao = 'BE', 
	nm_usuario_baixa_esp = nm_usuario_p 
where 	nr_prescricao = nr_prescricao_p 
and 	nr_sequencia = nr_seq_procedimento_p;
 
if (ie_informa_justif_p = 'S') then 
	update 	prescr_procedimento 
	set	nr_seq_motivo_just = nr_seq_motivo_just_p 
	where 	nr_prescricao = nr_prescricao_p 
	and 	nr_sequencia = nr_seq_procedimento_p;
end if;
 
if (coalesce(ie_atualizar_adep_p,'N') = 'S') then 
	CALL Baixar_horarios_proc_esp(nr_atendimento_p, nr_prescricao_p, nr_seq_procedimento_p, nm_usuario_p, '1');
end if;
 
select coalesce(max(ie_cancelar_autor),'N') ie_cancela 
into STRICT	ie_cancela_w 
from  tipo_baixa_prescricao 
where  ie_prescricao_devolucao = 'P' 
and   cd_tipo_baixa      = cd_motivo_baixa_p;
 
if (ie_cancela_w = 'S') then 
	CALL suspender_autor_convenio(nr_prescricao_p, nr_seq_procedimento_p, null, nm_usuario_p);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
