CREATE OR REPLACE PROCEDURE baixar_processo_atendimento (nr_atendimento_p bigint, cd_processo_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
dt_fim_real_w				timestamp;
ie_finalizar_atend_w			varchar(1);/* Rafael em 12/03/2007 OS51338 */
 

BEGIN 
/* Rafael em 12/03/2007 OS51338 */
 
select	coalesce(max(obter_valor_param_usuario(0, 73, obter_perfil_ativo, nm_usuario_p, 0)),'S') 
into STRICT	ie_finalizar_atend_w
;
 
select	max(dt_fim_real) 
into STRICT	dt_fim_real_w 
from	processo_atendimento 
where	nr_atendimento	= nr_atendimento_p 
and	cd_processo	= cd_processo_p 
and	nr_sequencia	= nr_sequencia_p;
 
if (coalesce(dt_fim_real_w::text, '') = '') then 
	update	processo_atendimento 
	set	dt_fim_real	= clock_timestamp(), 
		ie_status	= 'F', 
		nm_usuario	= nm_usuario_p, 
		dt_atualizacao	= clock_timestamp() 
	where	nr_atendimento	= nr_atendimento_p 
	and	cd_processo	= cd_processo_p 
	and	nr_sequencia	= nr_sequencia_p;
end if;
 
if (ie_finalizar_atend_w = 'S') then /* Rafael em 12/03/2007 OS51338 */
 
	update	atendimento_paciente 
	set	ie_fim_conta	= 'F', 
		dt_fim_conta 	= clock_timestamp() 
	where	nr_atendimento	= nr_atendimento_p 
	and	ie_fim_conta	= 'P' 
	and	(dt_alta IS NOT NULL AND dt_alta::text <> '') 
	and	not exists (SELECT nr_atendimento 
				from	processo_atendimento 
				where	nr_atendimento	= nr_atendimento_p 
				and	coalesce(dt_fim_real::text, '') = '');
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
