SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baixar_titulos_bordero_js ( nr_bordero_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


vl_credito_w	double precision;
vl_titulo_w	double precision;
qt_registro_w	bigint;
ds_erro_w	varchar(400);


BEGIN

if (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from 	movto_banco_pend_baixa
	where 	nr_bordero_rec = nr_bordero_p;

	if (qt_registro_w <> 0) then
		begin
		select 	sum(vl_baixa)
		into STRICT 	vl_credito_w
		from 	movto_banco_pend_baixa
		where 	nr_bordero_rec = nr_bordero_p;

		select 	coalesce(sum(vl_abaixar),0) +
			coalesce(sum(vl_juros),0) +
			coalesce(sum(vl_multa),0) +
			coalesce(sum(vl_rec_maior),0)
		into STRICT	vl_titulo_w
		from 	bordero_tit_rec
		where 	nr_bordero = nr_bordero_p;

		if (vl_credito_w <> vl_titulo_w) then
			begin
			ds_erro_w := substr(obter_texto_dic_objeto(105363, 1, 'vl_credito='
				|| campo_mascara_virgula(vl_credito_w)
				|| ';vl_titulo='
				|| campo_mascara_virgula(vl_titulo_w)), 1, 400);
			end;
		end if;
		end;
	else
		begin
			ds_erro_w := obter_texto_tasy(105365, 1);
		end;
	end if;
	end;
end if;
ds_erro_p	:= ds_erro_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
