SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baixar_titulo_desconto_folha ( nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w		bigint;
qt_titulo_w		bigint;
ds_ocorrencia_w		varchar(255);
vl_recebimento_w	double precision;
dt_recebimento_w	timestamp;
ds_observacao_w		varchar(255);
dt_emissao_w		timestamp;
nr_seq_desconto_folha_w	bigint;
nr_sequencia_w		bigint;
cd_moeda_w		integer;
cd_tipo_recebimento_w	integer;
cd_estabelecimento_w	smallint;
ie_baixa_w		varchar(1);

C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.nr_titulo, 
		a.vl_baixa, 
		a.dt_baixa, 
		substr(wheb_mensagem_pck.get_texto(303744,	'DS_TIPO_COLAB=' || a.ds_tipo_colaborador || ';' || 
								'DS_CAD_COLAB=' || a.ds_cadastro_colaborador || ';' || 
								'DS_TIPO_VALE=' || a.ds_tipo_vale || ';' || 
								'DT_DESC=' || to_char(a.dt_inicio_desconto,'mm/yyyy') || ';' || 
								'QT_PARCELA=' || a.qt_parcela || ';' || 
								'VL_PARCELA=' || to_char(a.vl_liquido,'99999990.00')),1,255) ds_observacao 
	from	w_desconto_folha a 
	where	coalesce(a.ie_baixa,'N')	= 'N' 
	and	a.nm_usuario		= nm_usuario_p;


BEGIN 
open C01;
loop 
fetch C01 into 
	nr_seq_desconto_folha_w, 
	nr_titulo_w, 
	vl_recebimento_w, 
	dt_recebimento_w, 
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
 
	ds_ocorrencia_w	:= null;
	ie_baixa_w	:= 'N';
 
	select	count(*), 
		max(a.dt_emissao), 
		max(cd_estabelecimento) 
	into STRICT	qt_titulo_w, 
		dt_emissao_w, 
		cd_estabelecimento_w 
	from	titulo_receber a 
	where	a.nr_titulo	= nr_titulo_w;
 
	if (coalesce(qt_titulo_w,0) = 0) then 
		ds_ocorrencia_w	:= substr(wheb_mensagem_pck.get_texto(303748),1,255);
	end if;
 
	if (coalesce(vl_recebimento_w,0) = 0) then 
		ds_ocorrencia_w	:= substr(ds_ocorrencia_w || wheb_mensagem_pck.get_texto(303749),1,255);
	end if;
 
	if (dt_recebimento_w < dt_emissao_w) then 
		ds_ocorrencia_w	:= substr(ds_ocorrencia_w || wheb_mensagem_pck.get_texto(303750),1,255);
	end if;
	 
	select	max(a.cd_moeda_padrao) 
	into STRICT	cd_moeda_w 
	from	parametro_contas_receber a 
	where	a.cd_estabelecimento	= cd_estabelecimento_w;
	 
	select	max(a.cd_tipo_recebimento) 
	into STRICT	cd_tipo_recebimento_w 
	from	tipo_recebimento a 
	where	a.ie_tipo_consistencia	= 14 
	and	a.cd_estabelecimento	= cd_estabelecimento_w;
 
	if (coalesce(ds_ocorrencia_w::text, '') = '') then 
		select	coalesce(max(a.nr_sequencia),0) + 1 
		into STRICT	nr_sequencia_w 
		from	titulo_receber_liq a 
		where	a.nr_titulo	= nr_titulo_w;
 
		/* fazer o insert na tabela TITULO_RECEBER_LIQ */
 
		 
		insert	into titulo_receber_liq(nr_sequencia, 
			nr_titulo, 
			ie_acao, 
			ie_lib_caixa, 
			cd_moeda, 
			cd_tipo_recebimento, 
			dt_atualizacao, 
			nm_usuario, 
			dt_recebimento, 
			vl_recebido, 
			vl_descontos, 
			vl_glosa, 
			vl_juros, 
			vl_multa, 
			vl_rec_maior, 
			ds_observacao) 
		values (nr_sequencia_w, 
			nr_titulo_w, 
			'I', 
			'S', 
			cd_moeda_w, 
			cd_tipo_recebimento_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			dt_recebimento_w, 
			vl_recebimento_w, 
			0, 
			0, 
			0, 
			0, 
			0, 
			ds_observacao_w);
		 
		CALL atualizar_saldo_tit_rec(nr_titulo_w,nm_usuario_p);
 
		ds_ocorrencia_w	:= substr(wheb_mensagem_pck.get_texto(303751),1,255);
		ie_baixa_w	:= 'S';
 
	else 
		ie_baixa_w	:= 'N';
	end if;
 
	update	w_desconto_folha 
	set	ds_ocorrencia	= ds_ocorrencia_w, 
		ie_baixa	= ie_baixa_w 
	where	nr_sequencia	= nr_seq_desconto_folha_w;
	 
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baixar_titulo_desconto_folha ( nm_usuario_p text) FROM PUBLIC;

