SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baixa_automatica_ordem_compra () AS $body$
DECLARE

 
 
nr_ordem_compra_w		bigint :=0;
contador_w			smallint;
nr_seq_motivo_baixa_aut_w	bigint;
cd_estabelecimento_w		smallint;

c01 CURSOR FOR 
SELECT	nr_ordem_compra, 
	cd_estabelecimento 
from	ordem_compra 
where	dt_ordem_compra < (clock_timestamp() - interval '180 days') 
and	coalesce(dt_baixa::text, '') = '';

 

BEGIN 
 
open	c01;
loop 
fetch	c01 into 
	nr_ordem_compra_w, 
	cd_estabelecimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	select	count(*) 
	into STRICT	contador_w 
 	from	ordem_compra_item_entrega a 
	where	nr_ordem_compra	= nr_ordem_compra_w 
	and	((coalesce(qt_prevista_entrega,0) > coalesce(qt_real_entrega,0)) and coalesce(dt_baixa::text, '') = '');
 
	select	nr_seq_motivo_baixa_aut 
	into STRICT	nr_seq_motivo_baixa_aut_w 
	from	parametro_compras 
	where	cd_estabelecimento = cd_estabelecimento_w;
 
	if (contador_w = 0) then 
		begin 
		update	ordem_compra 
		set	dt_baixa = clock_timestamp(), 
			nr_seq_motivo_baixa = nr_seq_motivo_baixa_aut_w 
		where 	nr_ordem_compra = nr_ordem_compra_w;
		end;
	end if;
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
