CREATE OR REPLACE PROCEDURE baixa_auto_solic_compra_lib ( cd_estabelecimento_p bigint, dt_parametro_p timestamp, dias_aberta_p bigint) AS $body$
DECLARE



nr_solic_compra_w	solic_compra.nr_solic_compra%type;

C01 CURSOR FOR
SELECT	nr_solic_compra
from 	solic_compra a
where 	a.dt_solicitacao_compra < (dt_parametro_p - dias_aberta_p)
and		a.cd_estabelecimento = cd_estabelecimento_p
and 	coalesce(a.dt_baixa::text, '') = ''
and     coalesce(a.nr_seq_motivo_cancel::text, '') = ''
and     not exists (select 1 from ordem_compra_item b where b.nr_solic_compra = a.nr_solic_compra)
and  	not exists (select 1 from	cot_compra_item b where b.nr_solic_compra = a.nr_solic_compra
                   
union
 select	1 from	cot_compra_solic_agrup c where	c.nr_solic_compra = a.nr_solic_compra)
and 	not exists (select 1 from solic_compra_item b where  b.nr_solic_compra = a.nr_solic_compra
		   and obter_bloq_canc_proj_rec(b.nr_seq_proj_rec) > 0);


BEGIN

open	c01;
loop
fetch	c01 into
	nr_solic_compra_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	update	solic_compra
	set	dt_baixa = clock_timestamp()
	where 	nr_solic_compra = nr_solic_compra_w;
	
	insert into solic_compra_hist(
		nr_sequencia,
		nr_solic_compra,
		dt_atualizacao,
		nm_usuario,
		dt_historico,
		ds_titulo,
		ds_historico,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_tipo,
		cd_evento,
		dt_liberacao)
	values (	nextval('solic_compra_hist_seq'),
		nr_solic_compra_w,
		clock_timestamp(),
		'Tasy',
		clock_timestamp(),
		Wheb_mensagem_pck.get_Texto(297835),
		Wheb_mensagem_pck.get_Texto(1198370),
		clock_timestamp(),
		'Tasy',
		'S',
		'B',
		clock_timestamp());	
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
