SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baixa_devolucao_movimento ( cd_material_p bigint, nr_devolucao_p bigint, nr_seq_lote_fornec_p bigint, qt_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_material_w 			bigint;
qt_saldo_mat_w			bigint;
ie_baixa_w				bigint;
qt_mat_devol_w			farm_devolucao_mov_item.qt_material%type;
cd_local_estoque_w 		farm_devolucao_mov_item.cd_local_estoque%type;
nr_seq_lote_fornec_w	farm_devolucao_mov_item.nr_seq_lote_fornec%type;
nr_atendimento_w		farm_devolucao_movimento.nr_atendimento%type;
dt_atendimento_w		farm_devolucao_mov_item.dt_atendimento%type;
cd_setor_atendimento_w	farm_devolucao_movimento.cd_setor_atendimento%type;
cd_unidade_medida_w		farm_devolucao_mov_item.cd_unidade_medida%type;
cd_cgc_fornec_w			farm_devolucao_mov_item.cd_cgc_fornec%type;
nr_sequencia_w			farm_devolucao_mov_item.nr_sequencia%type;
								
c01 CURSOR FOR 
	SELECT 	a.cd_local_estoque, 
			sum(a.qt_material) qt_material 
	from 	farm_devolucao_mov_item a 
	where 	a.nr_seq_devolucao 	= nr_devolucao_p 
	and 	a.cd_material 		= cd_material_p 
	and (coalesce(a.nr_seq_lote_fornec::text, '') = '' or a.nr_seq_lote_fornec = nr_seq_lote_fornec_p) 
	and 	coalesce(dt_recebimento::text, '') = '' 
	group by 
			a.cd_local_estoque;


BEGIN 
 
qt_material_w := qt_material_p;
 
open c01;
	loop 
	fetch c01 into 
	cd_local_estoque_w, 
	qt_mat_devol_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	qt_saldo_mat_w 	:= (qt_material_w - qt_mat_devol_w);
	 
	if (qt_material_w > qt_mat_devol_w)then 
		qt_material_w 	:= qt_mat_devol_w;
	end if;
 
	CALL gravar_log_tasy(1812, '1 qt_material_w=' || qt_material_w || ' qt_mat_devol_w=' || qt_mat_devol_w || ' qt_saldo_mat_w=' || qt_saldo_mat_w || ' cd_material_p=' || cd_material_p || 
	' cd_local_estoque_w=' || cd_local_estoque_w,nr_devolucao_p);
		 
	if (qt_material_w > 0)then 
		 
		select 	b.nr_sequencia, 
				c.nr_atendimento, 
				b.dt_atendimento, 
				c.cd_setor_atendimento, 
				b.cd_unidade_medida, 
				b.cd_cgc_fornec 
		into STRICT	nr_sequencia_w, 
				nr_atendimento_w, 
				dt_atendimento_w, 
				cd_setor_atendimento_w, 
				cd_unidade_medida_w, 
				cd_cgc_fornec_w 
		from	farm_devolucao_mov_item b, 
				farm_devolucao_movimento c 
		where 	b.nr_seq_devolucao 	= c.nr_sequencia 
		and 	c.nr_sequencia 		= nr_devolucao_p 
		and 	cd_material 		= cd_material_p 
		and 	cd_local_estoque 	= cd_local_estoque_w 
		and (coalesce(b.nr_seq_lote_fornec::text, '') = '' or b.nr_seq_lote_fornec = nr_seq_lote_fornec_p) 
		and 	coalesce(dt_recebimento::text, '') = '';
 
		CALL gerar_prescricao_estoque(cd_estabelecimento_p,nr_atendimento_w,null,cd_material_p,dt_atendimento_w,2,cd_local_estoque_w,(qt_material_w * -1),cd_setor_atendimento_w,cd_unidade_medida_w, 
		nm_usuario_p,'I',null,null,null,null,cd_cgc_fornec_w,'',nr_seq_lote_fornec_p,null,null,null,'','','');		
	end if;
	 
	qt_material_w := qt_saldo_mat_w;
 
	 
	CALL gravar_log_tasy(1812, '2 qt_material_w=' || qt_material_w || ' qt_mat_devol_w=' || qt_mat_devol_w || ' qt_saldo_mat_w=' || qt_saldo_mat_w || ' cd_material_p=' || cd_material_p || 
	' cd_local_estoque_w=' || cd_local_estoque_w,nr_devolucao_p);
	 
	update	farm_devolucao_mov_item 
	set 	dt_recebimento = clock_timestamp() 
	where 	nr_sequencia = nr_sequencia_w;
	 
	end;
end loop;
close c01;
 
select 	count(*) 
into STRICT	ie_baixa_w 
from	farm_devolucao_mov_item 
where	nr_seq_devolucao = nr_devolucao_p 
and		coalesce(dt_recebimento::text, '') = '';
 
if (ie_baixa_w = 0) then 
	CALL atualizar_datas_dev_mat_pac(nr_devolucao_p, 'ABM',nm_usuario_p);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
