SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baixa_mat_lib_prescr_retrogada ( nr_prescricao_p bigint) AS $body$
DECLARE



ie_prescr_emergencia_w	varchar(1);
cd_motivo_baixa_ret_w	bigint;
cd_estabelecimento_w	bigint;
qt_nr_seq_lote_w	bigint;


BEGIN

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from 	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	coalesce(max(cd_motivo_baixa_ret),0)
into STRICT	cd_motivo_baixa_ret_w
from 	parametros_farmacia
where	cd_estabelecimento = cd_estabelecimento_w;

select 	count(*)
into STRICT 	ie_prescr_emergencia_w
from 	prescr_medica
where 	nr_prescricao = nr_prescricao_p
and  	ie_prescr_emergencia = 'S';

if (ie_prescr_emergencia_w > 0) and (cd_motivo_baixa_ret_w > 0) then

	update	prescr_material
	set	cd_motivo_baixa	= cd_motivo_baixa_ret_w
	where	nr_prescricao	= nr_prescricao_p;
end if;

select	count(*)
into STRICT	qt_nr_seq_lote_w
from	ap_lote
where	nr_prescricao = nr_prescricao_p;

if (coalesce(qt_nr_seq_lote_w,0) >  0) and (cd_motivo_baixa_ret_w > 0) and (ie_prescr_emergencia_w > 0) then

	update ap_lote
	set  ie_status_lote = 'C'
	where nr_prescricao = nr_prescricao_p;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
