SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baixa_solic_pront_pend ( qt_dias_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;

C01 CURSOR FOR
SELECT	nr_sequencia
from	same_cpi_solic
where (ie_status_solic = 'A')
and	(dt_desejada < (clock_timestamp() - qt_dias_p));


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

update	same_cpi_solic
set	ie_status_solic		= 'C',
	dt_cancelamento 	= clock_timestamp(),
	nm_usuario_cancel	= 'Job'
where	nr_sequencia 		= nr_sequencia_w;
END LOOP;
CLOSE C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
