CREATE OR REPLACE PROCEDURE baixa_total_titulo ( nr_titulo_p bigint, cd_tipo_baixa_p bigint, vl_baixa_p bigint, vl_pago_p bigint, vl_devolucao_p bigint, vl_juros_p bigint, vl_multa_p bigint, vl_descontos_p bigint, vl_outras_deducoes_p bigint, vl_outros_acrescimos_p bigint, vl_ir_p bigint, dt_baixa_p timestamp, nr_seq_conta_banco_p bigint, nr_bordero_p bigint, nr_seq_escrit_p bigint, nr_seq_trans_fin_p bigint, ie_conta_contabil_baixa_p text, ie_baixa_bloqueto_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

		
cd_moeda_w		integer;
nr_seq_trans_fin_w	bigint;
cd_conta_contabil_w	varchar(20);
nr_sequencia_w		integer;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	begin

	select 	cd_moeda
	into STRICT	cd_moeda_w
	from 	titulo_pagar
	where 	nr_titulo = nr_titulo_p;		

	if (coalesce(cd_moeda_w::text, '') = '') then
		select 	cd_moeda_padrao
		into STRICT	cd_moeda_w
		from 	parametros_contas_pagar
		where 	cd_estabelecimento = cd_estabelecimento_p;
	end if;	

	if (nr_seq_trans_fin_p <> 0) then
		nr_seq_trans_fin_w	:= nr_seq_trans_fin_p;
	elsif (cd_tipo_baixa_p <> 0) then
		select  coalesce(max(a.nr_seq_trans_fin),0)
		into STRICT	nr_seq_trans_fin_w
		from    tipo_baixa_cpa a
		where  	a.cd_tipo_baixa = cd_tipo_baixa_p;

		if (nr_seq_trans_fin_w = 0) then
			nr_seq_trans_fin_w := null;
		end if;
	end if;

	if (ie_conta_contabil_baixa_p = 'S') then
		select 	max(cd_conta_contabil)
		into STRICT	cd_conta_contabil_w
		from 	titulo_pagar_classif 
		where 	nr_titulo = nr_titulo_p;
	end if;

	select	max(nr_sequencia) + 1
	into STRICT	nr_sequencia_w
	from	titulo_pagar_baixa
	where	nr_titulo	= nr_titulo_p;

	if (coalesce(nr_sequencia_w::text, '') = '') then
		nr_sequencia_w := 1;
	end if;

	insert into titulo_pagar_baixa(
		nr_sequencia,
		nr_titulo,
		cd_tipo_baixa,
		vl_baixa,
		vl_pago,
		vl_devolucao,
		vl_juros,
		vl_multa,
		vl_descontos,
		vl_outras_deducoes,
		vl_outros_acrescimos,
		vl_ir,
		dt_baixa,
		cd_moeda,
		nr_seq_conta_banco,
		nr_bordero,
		nr_seq_escrit,
		nr_seq_trans_fin,
		cd_conta_contabil,
		ie_baixa_bloqueto,
		dt_atualizacao,
		ie_acao,
		nm_usuario,
		vl_imposto_munic,
		vl_inss,
		vl_glosa,
		vl_outras_despesas,
		nr_lote_contabil,
		vl_imposto)
	values (nr_sequencia_w,
		nr_titulo_p,
		cd_tipo_baixa_p,
		vl_baixa_p,
		vl_pago_p,
		vl_devolucao_p,
		vl_juros_p,
		vl_multa_p,
		vl_descontos_p,
		vl_outras_deducoes_p,
		vl_outros_acrescimos_p,
		vl_ir_p,
		dt_baixa_p,
		cd_moeda_w,
		nr_seq_conta_banco_p,
		nr_bordero_p,
		nr_seq_escrit_p,
		nr_seq_trans_fin_w,
		cd_conta_contabil_w,
		ie_baixa_bloqueto_p,
		clock_timestamp(),
		'I',
		nm_usuario_p,
		0,
		0,
		0,
		0,
		0,
		0);

	CALL atualizar_tit_pagar_baixa(nr_titulo_p,nr_sequencia_w,nm_usuario_p);

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
