CREATE OR REPLACE PROCEDURE baka_atualizar_peso_pacate () AS $body$
BEGIN

update paciente_atendimento
set qt_peso = replace(qt_peso, '.', ',')
where (qt_peso IS NOT NULL AND qt_peso::text <> '')
and qt_peso like '%.%';

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
