CREATE OR REPLACE PROCEDURE barras_quimio_js ( CD_MATERIAL_P bigint, QT_DOSE_ML_P bigint, NR_SEQ_ORDEM_P bigint, CD_UNIDADE_MEDIDA_PRESCR_P text, QT_TOTAL_CABINE_ML_P bigint, QT_DOSE_SALDO_P bigint, QT_DOSE_UTIL_ML_P bigint, NM_USUARIO_P text, QT_DOSE_DESCONTO_P INOUT bigint) AS $body$
DECLARE


qt_dose_aux_w	double precision;


BEGIN



if (qt_dose_ml_p <= qt_total_cabine_ml_p) then

	qt_dose_aux_w := Obter_dose_conv_quimio_ordem(cd_material_p, qt_dose_saldo_p, obter_unid_med_usua('ml'), cd_unidade_medida_prescr_p, nr_seq_ordem_p);

    if (qt_dose_aux_w >= qt_dose_saldo_p) then
		qt_dose_desconto_p	:= qt_dose_saldo_p;
    end if;

else

	if (Upper(cd_unidade_medida_prescr_p) <> upper(obter_unid_med_usua('ml'))) then
		qt_dose_desconto_p := Obter_dose_conv_quimio_ordem(cd_material_p, qt_total_cabine_ml_p, obter_unid_med_usua('ml'), cd_unidade_medida_prescr_p, nr_seq_ordem_p);
    else
		qt_dose_desconto_p := qt_total_cabine_ml_p;
	end if;

end if;

if (qt_dose_desconto_p >= qt_dose_saldo_p) then

	qt_dose_desconto_p := Obter_dose_conv_quimio_ordem(cd_material_p, qt_dose_saldo_p, cd_unidade_medida_prescr_p, obter_unid_med_usua('ml'), nr_seq_ordem_p);

else
	if (Upper(cd_unidade_medida_prescr_p) <> upper(obter_unid_med_usua('ml'))) then
		qt_dose_desconto_p := Obter_dose_conv_quimio_ordem(cd_material_p, qt_dose_desconto_p, cd_unidade_medida_prescr_p, obter_unid_med_usua('ml'), nr_seq_ordem_p);
	end if;
end if;

end;
$body$
