CREATE OR REPLACE PROCEDURE before_profisssionais_eup_js ( nm_usuario_p text, IE_TIPO_PROFISSIONAL_P text, CD_MEDICO_P text, NM_MEDICO_EXTERNO_P text, CRM_MEDICO_EXTERNO_P text, DS_MSG_ERRO_P INOUT text, DS_MSG_ALERTA_P INOUT text, IE_PERMITE_MEDICO_EXTERNO_P INOUT text) AS $body$
DECLARE

ie_medico_w			varchar(1);
cd_estabelecimento_w		bigint;
ds_msg_erro_w			varchar(255);
ie_continua_w			varchar(1) := 'S';
ie_obriga_medico_externo_w	varchar(1);
cd_msg_medico_w			bigint;
ds_msg_alerta_w			varchar(255);
ie_codigo_med_externo_w		varchar(60);
ie_permite_medico_externo_w	varchar(1) := 'N';


BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

ie_obriga_medico_externo_w := Obter_param_Usuario(916, 1056, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_obriga_medico_externo_w);
ie_codigo_med_externo_w := Obter_param_Usuario(916, 125, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_codigo_med_externo_w);

ie_medico_w := obter_se_medico(cd_medico_p, 'M');

if (ie_tipo_profissional_p = '1') and (ie_medico_w = 'N') then

	ds_msg_erro_w := substr(obter_texto_tasy(74926, null),1,255);
	ie_continua_w := 'N';
end if;

if (ie_continua_w = 'S') then

	if (ie_codigo_med_externo_w IS NOT NULL AND ie_codigo_med_externo_w::text <> '') and (ie_codigo_med_externo_w = cd_medico_p) and
		((coalesce(nm_medico_externo_p::text, '') = '') or (coalesce(crm_medico_externo_p::text, '') = '')) then

		ie_permite_medico_externo_w := 'S';

		cd_msg_medico_w := obter_msg_medico_externo(ie_obriga_medico_externo_w,nm_medico_externo_p,crm_medico_externo_p);
		if (cd_msg_medico_w <> 0) then

			ds_msg_alerta_w := substr(obter_texto_tasy(cd_msg_medico_w, null),1,255);
		end if;
	end if;
end if;

ds_msg_erro_p := ds_msg_erro_w;
ds_msg_alerta_p	:= ds_msg_alerta_w;
ie_permite_medico_externo_p := ie_permite_medico_externo_w;

commit;

end;
$body$
