SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE bft_request_vital_signs_job ( nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	b.nr_atendimento, a.nm_leito_integracao
	from	unidade_atendimento a,
		atendimento_paciente b
	where	a.nr_atendimento = b.nr_atendimento
	and	a.ie_leito_monitorado = 'S' 
	and	(a.nm_leito_integracao IS NOT NULL AND a.nm_leito_integracao::text <> '')
	and	a.ie_situacao = 'A';			
			
BEGIN

for r_c01 in c01 loop
	begin
		CALL bft_request_vital_signs( r_c01.nr_atendimento, nm_usuario_p);
	exception
	when others then
		null;
	end;
end loop;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
