SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE biob_alterar_status (nr_seq_item_mapa_p bigint, ie_novo_status_p text, nm_usuario_p text, ie_salvou_registro_p INOUT text) AS $body$
DECLARE


ie_status_valido_w	varchar(1);
ie_salvou_registro_w	varchar(1);
ie_mapa_usado_w		varchar(1);


BEGIN

	ie_salvou_registro_w := 'N';

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_status_valido_w
	from	valor_dominio
	where	cd_dominio = 6101
	and	vl_dominio = ie_novo_status_p;

	if (ie_status_valido_w = 'S'
		and (nr_seq_item_mapa_p IS NOT NULL AND nr_seq_item_mapa_p::text <> '')
		and nr_seq_item_mapa_p <> 0) then

		ie_mapa_usado_w := biob_obter_se_mapa_usado(nr_seq_item_mapa_p);

		if (ie_mapa_usado_w = 'S') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(360074);
		end if;

		update biob_mapa_localizacao
		set ie_status_localizacao = ie_novo_status_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where nr_sequencia = nr_seq_item_mapa_p;

		commit;

		ie_salvou_registro_w := 'S';

	end if;

	ie_salvou_registro_p := ie_salvou_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
