CREATE OR REPLACE PROCEDURE biob_excluir_mapa_localizacao ( nr_seq_freezer_p bigint, ie_excluir_p INOUT text) AS $body$
DECLARE



nr_seq_mapa_w	bigint;
ie_excluir_w	varchar(1) := 'S';

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	biob_mapa_localizacao a
	where	a.nr_seq_freezer = nr_seq_freezer_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_mapa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_excluir_w <> 'N') then
		select	coalesce((	select	max('N')
				from	biob_coleta_tubo a
				where	a.nr_seq_mapa_loc = nr_seq_mapa_w),'S')
		into STRICT	ie_excluir_w
		;
	end if;

	/*if (ie_excluir_w <> 'N') then
		select	nvl((	select	'N'
				from	biob_coleta_lamina a
				where	a.nr_seq_mapa_loc = nr_seq_mapa_w),'S')
		into	ie_excluir_w
		from	dual;
	end if;*/
	end;
end loop;
close C01;

ie_excluir_p := ie_excluir_w;

if (ie_excluir_w <> 'N') then
	delete	FROM biob_mapa_localizacao a
	where	a.nr_seq_freezer = nr_seq_freezer_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
