CREATE OR REPLACE PROCEDURE bloquear_exame_js ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_status_atend_p bigint, nr_seq_motivo_bloq_p bigint, ds_observacao_p text, ie_forma_boqueio_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_forma_boqueio_p = 'S') then

	update	prescr_procedimento
	set 	ie_status_atend = ie_status_atend_p,
		nm_usuario 	= nm_usuario_p
	where 	nr_prescricao 	= nr_prescricao_p
	and 	nr_sequencia 	= nr_sequencia_p;
	commit;

elsif (ie_forma_boqueio_p = 'B') then

	if (nr_seq_motivo_bloq_p > 0) then

		update	prescr_procedimento
		set    	ie_exame_bloqueado = 'S',
			nm_usuario 	= nm_usuario_p,
			nr_seq_motivo_bloqueio = nr_seq_motivo_bloq_p,
			ds_observacao 	= ds_observacao || ' '|| ds_observacao_p
		where  	nr_sequencia 	= nr_sequencia_p
		and    	nr_prescricao 	= nr_prescricao_p;
		commit;
	else
		update	prescr_procedimento
		set    	ie_exame_bloqueado = 'S',
			nm_usuario 	= nm_usuario_p,
			ds_observacao 	= ds_observacao || ' '|| ds_observacao_p
		where  	nr_sequencia 	= nr_sequencia_p
		and    	nr_prescricao 	= nr_prescricao_p;
		commit;
	end if;
end if;

end;
$body$
