CREATE OR REPLACE PROCEDURE bloquear_exame_visualizacao_js (nr_seq_resultado_p bigint, nr_seq_prescr_p bigint, ie_opcao_p text) AS $body$
DECLARE


/*
ie_opcao_p: B - BLoquear
	D - Desbloquear
*/
BEGIN

if (ie_opcao_p = 'B') then

	update 	exame_lab_result_item
	set 	ie_restringe_resultado = 'S'
	where 	nr_seq_resultado = nr_seq_resultado_p
	and 	nr_seq_prescr = nr_seq_prescr_p
	and 	(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');
	commit;
elsif (ie_opcao_p = 'D') then

	update 	exame_lab_result_item
	set 	ie_restringe_resultado = 'N',
		nr_seq_motivo_bloqueio  = NULL,
		ds_obs_bloq_result  = NULL
	where 	nr_seq_resultado = nr_seq_resultado_p
	and 	nr_seq_prescr = nr_seq_prescr_p
	and 	(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
