CREATE OR REPLACE PROCEDURE bloquear_geracao_titulo ( nr_atendimento_p bigint, nr_seq_motivo_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


ie_atend_bloqueado_w	varchar(1);


BEGIN

select 	coalesce(max(obter_se_atend_bloqueado(nr_atendimento_p)),'N')
into STRICT	ie_atend_bloqueado_w
;

insert into bloqueio_titulo_paciente(NR_SEQUENCIA,
	NR_ATENDIMENTO,
	DT_ATUALIZACAO,
	NM_USUARIO,
	DT_ATUALIZACAO_NREC,
	NM_USUARIO_NREC,
	NR_SEQ_MOTIVO,
	IE_BLOQUEIO,
	DS_OBSERVACAO)
values (nextval('bloqueio_titulo_paciente_seq'),
	nr_atendimento_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	CASE WHEN nr_seq_motivo_p=0 THEN null  ELSE nr_seq_motivo_p END ,
	CASE WHEN ie_atend_bloqueado_w='S' THEN  'D'  ELSE 'B' END ,
	ds_observacao_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
