CREATE OR REPLACE PROCEDURE bloqueia_fatur_cirurgia ( nr_seq_agenda_p bigint, nr_cirurgia_p bigint, ie_acao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
BEGIN

if (ie_acao_p IS NOT NULL AND ie_acao_p::text <> '') and
	((coalesce(nr_seq_agenda_p,0) > 0) or (coalesce(nr_cirurgia_p,0) > 0)) then
	update	agenda_paciente
	set	dt_bloqueio_faturamento = CASE WHEN ie_acao_p='B' THEN clock_timestamp()  ELSE null END ,
		nm_usuario_bloqueio_fat = CASE WHEN ie_acao_p='B' THEN nm_usuario_p  ELSE null END
	where	nr_sequencia 		= nr_seq_agenda_p;

	update	cirurgia
	set	dt_bloqueio_faturamento = CASE WHEN ie_acao_p='B' THEN clock_timestamp()  ELSE null END ,
		nm_usuario_bloqueio_fat = CASE WHEN ie_acao_p='B' THEN nm_usuario_p  ELSE null END
	where	nr_cirurgia 		= nr_cirurgia_p;

	insert	into	hist_bloqueio_conta_cirur(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_cirurgia,
							nr_seq_agenda,
							ie_acao,
							dt_acao,
							nm_usuario_acao)
	values (nextval('hist_bloqueio_conta_cirur_seq'),
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							CASE WHEN nr_cirurgia_p=0 THEN null  ELSE nr_cirurgia_p END ,
							CASE WHEN nr_seq_agenda_p=0 THEN null  ELSE nr_seq_agenda_p END ,
							ie_acao_p,
							clock_timestamp(),
							nm_usuario_p);
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
