CREATE OR REPLACE PROCEDURE bloqueia_suplemento_oral (nr_atendimento_p bigint, dt_horario_p timestamp, ie_opcao_p text) AS $body$
DECLARE


nr_prescricao_w			bigint;
dt_bloqueio_sup_w		timestamp;

c01 CURSOR FOR
	SELECT	a.dt_bloqueio,
		a.nr_prescricao
	FROM	prescr_mat_hor a,
		prescr_medica b
	WHERE	a.ie_agrupador = 12
	AND	a.nr_prescricao = b.nr_prescricao
	AND	b.nr_atendimento = nr_atendimento_p
	AND	(a.dt_lib_horario IS NOT NULL AND a.dt_lib_horario::text <> '')
	AND	coalesce(a.dt_suspensao::text, '') = ''
	AND	coalesce(a.dt_fim_horario::text, '') = ''
	AND	a.dt_horario >= dt_horario_p;

BEGIN
open c01;
loop
fetch c01 into	dt_bloqueio_sup_w,
		nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(dt_bloqueio_sup_w::text, '') = '') and (ie_opcao_p = 'B') then
		UPDATE	prescr_mat_hor
		SET	dt_bloqueio = clock_timestamp()
		WHERE	nr_prescricao = nr_prescricao_w
		AND	(dt_lib_horario IS NOT NULL AND dt_lib_horario::text <> '')
		AND	coalesce(dt_suspensao::text, '') = ''
		AND	coalesce(dt_fim_horario::text, '') = ''
		AND	dt_horario >= dt_horario_p
		AND	ie_agrupador = 12;
		commit;
	elsif (dt_bloqueio_sup_w IS NOT NULL AND dt_bloqueio_sup_w::text <> '') and (ie_opcao_p = 'D') then
		UPDATE	prescr_mat_hor
		SET	dt_bloqueio  = NULL
		WHERE	nr_prescricao = nr_prescricao_w
		AND	(dt_lib_horario IS NOT NULL AND dt_lib_horario::text <> '')
		AND	coalesce(dt_suspensao::text, '') = ''
		AND	coalesce(dt_fim_horario::text, '') = ''
		AND	dt_horario >= dt_horario_p
		AND	ie_agrupador = 12;
		commit;
	end if;
	end;
end loop;
close c01;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
