SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE bl_alterar_status_leite ( nr_seq_frasco_p bigint, ie_status_p text, nm_usuario_p text) AS $body$
DECLARE


/*
C - Leite cr√∫
G - Leite congelado
P - Leite pasteurizado
*/
nr_seq_status_leite_w	bigint;
nr_seq_status_ant_w	bigint;


BEGIN
if (nr_seq_frasco_p IS NOT NULL AND nr_seq_frasco_p::text <> '') then

	select	max(nr_seq_status_leite)
	into STRICT	nr_seq_status_ant_w
	from	bl_frasco
	where	nr_sequencia = nr_seq_frasco_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_status_leite_w
	from	bl_status_leite
	where	ie_status = ie_status_p
	and	coalesce(ie_situacao,'A') = 'A';

	if (nr_seq_status_leite_w IS NOT NULL AND nr_seq_status_leite_w::text <> '') then

		update	bl_frasco
		set	nr_seq_status_leite 	= nr_seq_status_leite_w,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			ie_situacao_processa	= CASE WHEN ie_status_p='P' THEN 'N'  ELSE ie_situacao_processa END
		where	nr_sequencia		= nr_seq_frasco_p;

	end if;

	insert into bl_alteracao_status(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_frasco,
		nr_seq_status_leite)
	values (	nextval('bl_alteracao_status_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_frasco_p,
		nr_seq_status_ant_w);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
