CREATE OR REPLACE PROCEDURE bl_confirmar_saida ( nr_seq_saida_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_saida_p IS NOT NULL AND nr_seq_saida_p::text <> '') then

	update	bl_saida
	set	dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_fechamento		= clock_timestamp(),
		nm_usuario_fechamento	= nm_usuario_p
	where	nr_sequencia		= nr_seq_saida_p
	and	coalesce(nm_usuario_fechamento::text, '') = '';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
