SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE bl_definir_frasco_padrao ( nr_seq_agrup_frasco_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_agrupamento_w	bigint;


BEGIN
if (nr_seq_agrup_frasco_p IS NOT NULL AND nr_seq_agrup_frasco_p::text <> '') then

	select	max(nr_seq_agrupamento)
	into STRICT	nr_seq_agrupamento_w
	from	bl_agrup_frasco
	where	nr_sequencia = nr_seq_agrup_frasco_p;

	if (nr_seq_agrupamento_w IS NOT NULL AND nr_seq_agrupamento_w::text <> '') then

		update	bl_agrup_frasco
		set	ie_frasco_padrao = CASE WHEN nr_sequencia=nr_seq_agrup_frasco_p THEN 'S'  ELSE 'N' END ,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_seq_agrupamento = nr_seq_agrupamento_w;

		update	bl_agrupamento
		set	ie_novo_frasco = 'N'
		where	nr_sequencia = nr_seq_agrupamento_w;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
