CREATE OR REPLACE PROCEDURE bl_fracionar_leite ( nr_seq_frasco_p bigint, qt_volume_frac_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_volume_w		bigint;
nr_seq_frasco_w		bigint;
				

BEGIN
if (nr_seq_frasco_p IS NOT NULL AND nr_seq_frasco_p::text <> '') then

	select	nextval('bl_frasco_seq')
	into STRICT	nr_seq_frasco_w
	;

	insert into bl_frasco(	
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_coleta,
		dt_frasco,
		qt_volume,
		ds_observacao,
		qt_volume_inicial,
		nr_seq_motivo_inutil,
		nm_usuario_inutil,
		ie_local_inutilizacao,
		dt_utilizacao,
		nm_usuario_utilizacao,
		cd_frasco,
		cd_pessoa_dest,
		nr_seq_status_leite,
		nr_seq_origem,
		dt_inutilizacao,
		ie_situacao_processa,
		nr_seq_entrada,
		nm_doadora,
        cd_pessoa_fisica,
        ie_classif_leite)
	SELECT	nr_seq_frasco_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_coleta,
		clock_timestamp(),
		qt_volume_frac_p,
		ds_observacao,
		qt_volume_frac_p,
		nr_seq_motivo_inutil,
		nm_usuario_inutil,
		ie_local_inutilizacao,
		dt_utilizacao,
		nm_usuario_utilizacao,
		null,
		cd_pessoa_dest,
		nr_seq_status_leite,
		nr_seq_frasco_p,
		dt_inutilizacao,
		ie_situacao_processa,
		nr_seq_entrada,
		nm_doadora,
        cd_pessoa_fisica,
        ie_classif_leite
	from	bl_frasco
	where	nr_sequencia = nr_seq_frasco_p;

	select	qt_volume
	into STRICT	qt_volume_w
	from	bl_frasco
	where	nr_sequencia = nr_seq_frasco_p;
	
	update	bl_frasco
	set	qt_volume 	= qt_volume_w - qt_volume_frac_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia 	= nr_seq_frasco_p;
	
	insert into bl_alteracao_volume_frasco(	
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_frasco,
		qt_volume_ant,
		qt_volume_novo,
		ie_motivo_alteracao)
	values (	nextval('bl_alteracao_volume_frasco_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_frasco_p,
		qt_volume_w,
		qt_volume_w - qt_volume_frac_p,
		'F');
		
	CALL BL_gravar_codigo_frasco(nr_seq_frasco_w,'2',nm_usuario_p);
	
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
