SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE bl_gerar_ciclo ( nr_seq_pasteuriza_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_fase_w		varchar(1);
qt_intervalo_w		integer;
qt_operacao_w		integer;
i			integer;
cd_pessoa_fisica_w	varchar(10);
nr_seq_variavel_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	bl_variavel_medicao
	where	coalesce(ie_situacao,'A') = 'A'
	order by 1;

C02 CURSOR FOR
	SELECT	ie_fase,
		qt_intervalo,
		qt_operacao
	from	bl_regra_ciclo
	where	coalesce(ie_situacao,'A') = 'A'
	order by nr_seq_apresent;


BEGIN
if (nr_seq_pasteuriza_p IS NOT NULL AND nr_seq_pasteuriza_p::text <> '') then

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	bl_pasteurizacao
	where	nr_sequencia = nr_seq_pasteuriza_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_variavel_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		open C02;
		loop
		fetch C02 into
			ie_fase_w,
			qt_intervalo_w,
			qt_operacao_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			for i in 1..qt_operacao_w loop
				begin

				insert into bl_ciclo(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					dt_ciclo,
					cd_pessoa_fisica,
					ie_fase,
					ds_justificativa,
					nr_seq_pasteurizacao,
					qt_valor,
					nr_seq_variavel,
					vl_horario)
				values(	nextval('bl_ciclo_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					cd_pessoa_fisica_w,
					ie_fase_w,
					null,
					nr_seq_pasteuriza_p,
					null,
					nr_seq_variavel_w,
					qt_intervalo_w * (i-1));

				end;
			end loop;

			end;
		end loop;
		close C02;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE bl_gerar_ciclo ( nr_seq_pasteuriza_p bigint, nm_usuario_p text) FROM PUBLIC;

