SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE bl_gerar_exame_realizado ( ie_tipo_exame_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_lote_w		bigint;
nr_exames_w		bigint;
nr_seq_exame_w		bigint;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	bl_exame a 
	where	coalesce(a.ie_situacao,'A') = 'A' 
	and	CASE WHEN ie_tipo_exame_p=1 THEN a.ie_doadora WHEN ie_tipo_exame_p=9 THEN a.ie_pasteurizacao END  = 'S' 
	order by 1;


BEGIN 
 
begin 
select 	max(nr_sequencia) 
into STRICT	nr_seq_lote_w 
from 	bl_lote_exame 
where	CASE WHEN ie_tipo_exame_p=1 THEN nr_seq_doacao WHEN ie_tipo_exame_p=9 THEN nr_seq_frasco END  = nr_sequencia_p 
having	(max(nr_sequencia) IS NOT NULL AND (max(nr_sequencia))::text <> '');
exception 
	when no_data_found then 
	begin 
 
	select nextval('bl_lote_exame_seq') 
	into STRICT nr_seq_lote_w 
	;
 
	insert into bl_lote_exame( 
		nr_sequencia, 
		cd_pf_realizou, 
		dt_lote, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_doacao, 
		nr_seq_frasco) 
	values (nr_seq_lote_w, 
		Obter_Pf_Usuario(nm_usuario_p,'C'), 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		CASE WHEN ie_tipo_exame_p=1 THEN nr_sequencia_p  ELSE null END , 
		CASE WHEN ie_tipo_exame_p=9 THEN nr_sequencia_p  ELSE null END );
		 
	end;
end;
 
select	count(*) 
into STRICT 	nr_exames_w 
from 	bl_exame_realizado 
where 	nr_seq_lote_exame = nr_seq_lote_w;
 
if (nr_exames_w = 0) then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_exame_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		insert into bl_exame_realizado(	 
			nr_seq_lote_exame, 
			nr_seq_exame, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			dt_realizado) 
		values (	nr_seq_lote_w, 
			nr_seq_exame_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp());
		 
		end;
	end loop;
	close C01;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
