CREATE OR REPLACE PROCEDURE bl_iniciar_finalizar_coleta ( nr_seq_coleta_p bigint, ie_opcao_p text, dt_inicio_fim_coleta_p timestamp, nm_usuario_p text) AS $body$
DECLARE

/*
ie_opcao_p
I - Iniciar
F - Finalizar
*/
dt_inicio_fim_w timestamp;
					

BEGIN
    dt_inicio_fim_w := coalesce(dt_inicio_fim_coleta_p, clock_timestamp());

if (nr_seq_coleta_p IS NOT NULL AND nr_seq_coleta_p::text <> '') then

	if (ie_opcao_p = 'I') then
		update	bl_coleta
		set	dt_inicio_coleta = dt_inicio_fim_w,
			nm_usuario_inicio = nm_usuario_p
		where	nr_sequencia 	= nr_seq_coleta_p;
	elsif (ie_opcao_p = 'F') then
		update	bl_coleta
		set	dt_fim_coleta	= dt_inicio_fim_w,
			nm_usuario_fim	= nm_usuario_p
		where	nr_sequencia	= nr_seq_coleta_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
