SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE bl_iniciar_pasteurizacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_w		bigint;
nr_seq_frasco_w		bigint;
					
C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	bl_frasco_v a,
		bl_lote_item b
	where	a.nr_sequencia = b.nr_seq_frasco
	and 	coalesce(a.nr_seq_motivo_inutil::text, '') = ''
	and	b.nr_seq_lote = nr_seq_lote_w;
					

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	update	bl_pasteurizacao
	set	dt_inicio = clock_timestamp(),
		nm_usuario_inicio = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

	CALL BL_gerar_ciclo(nr_sequencia_p,nm_usuario_p);

	select	nr_seq_lote
	into STRICT	nr_seq_lote_w
	from	bl_pasteurizacao
	where	nr_sequencia = nr_sequencia_p;
	
	open C01;
	loop
	fetch C01 into	
		nr_seq_frasco_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		update	bl_frasco
		set	ie_situacao_processa = 'G',
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_frasco_w;
		
		end;
	end loop;
	close C01;
	
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
