CREATE OR REPLACE PROCEDURE bl_liberar_exame ( nr_seq_exame_p bigint, nr_seq_lote_exame_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


/*
ie_opcao_p
E - Exme selecionado
T - Todos os exames
*/
BEGIN
if (nr_seq_lote_exame_p IS NOT NULL AND nr_seq_lote_exame_p::text <> '') then

	if (ie_opcao_p = 'E') and (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') then

		update	bl_exame_realizado
		set	dt_liberacao 	= clock_timestamp(),
			nm_usuario_lib 	= nm_usuario_p
		where	nr_seq_lote_exame = nr_seq_lote_exame_p
		and	nr_seq_exame	= nr_seq_exame_p;

	elsif (ie_opcao_p = 'T') then

		update	bl_exame_realizado
		set	dt_liberacao 	= clock_timestamp(),
			nm_usuario_lib 	= nm_usuario_p
		where	nr_seq_lote_exame = nr_seq_lote_exame_p
		and	coalesce(to_char(vl_resultado),ds_resultado) is not null
		and	coalesce(dt_liberacao::text, '') = '';

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
