SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE bl_registrar_coleta ( nr_seq_doacao_p bigint, nr_seq_agenda_p bigint, dt_coleta_p timestamp, cd_frasco_p text, qt_volume_p bigint, ie_classif_coleta_p text, ie_procedencia_p text, ds_observacao_p text, nm_usuario_p text, dt_inicio_coleta_p timestamp, dt_fim_coleta_p timestamp) AS $body$
DECLARE


				
nr_seq_coleta_w		bigint;
nr_seq_status_leite_w	bigint;
nr_seq_frasco_w		bigint;
				

BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and (dt_coleta_p IS NOT NULL AND dt_coleta_p::text <> '') and (ie_procedencia_p IS NOT NULL AND ie_procedencia_p::text <> '') then

	select	nextval('bl_coleta_seq')
	into STRICT	nr_seq_coleta_w
	;

	insert into bl_coleta(	
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_doacao,
		nr_seq_agenda,
		dt_coleta,
		ds_observacao,
		ie_procedencia,
		ie_classif_leite,
		dt_inicio_coleta,
		nm_usuario_inicio,
		dt_fim_coleta,
		nm_usuario_fim)
	values (	nr_seq_coleta_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_doacao_p,
		nr_seq_agenda_p,
		dt_coleta_p,
		ds_observacao_p,
		ie_procedencia_p,
		ie_classif_coleta_p,
		dt_inicio_coleta_p,
		nm_usuario_p,
		dt_fim_coleta_p,
		nm_usuario_p);
		
	if (nr_seq_coleta_w IS NOT NULL AND nr_seq_coleta_w::text <> '') then
	
		select	max(nr_sequencia)
		into STRICT	nr_seq_status_leite_w
		from	bl_status_leite
		where	ie_status = 'C'
		and	coalesce(ie_situacao,'A') = 'A';
		
		select	nextval('bl_frasco_seq')
		into STRICT	nr_seq_frasco_w
		;
	
		insert into bl_frasco(	
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_coleta,
			dt_frasco,
			qt_volume,
			ds_observacao,
			qt_volume_inicial,
			cd_frasco,
			nr_seq_status_leite,
			ie_situacao_processa)
		values (	nr_seq_frasco_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_coleta_w,
			dt_coleta_p,
			coalesce(qt_volume_p,0),
			ds_observacao_p,
			coalesce(qt_volume_p,0),			
			cd_frasco_p,
			nr_seq_status_leite_w,
			'N');
			
			CALL BL_gravar_codigo_frasco(nr_seq_frasco_w, 0, nm_usuario_p);
	
	end if;	
end if;
		
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
