CREATE OR REPLACE PROCEDURE bl_vincular_desvincular_atend (nr_seq_doacao_p bigint, nr_atendimento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
V - Vincular
D - Desvincular
*/
BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	if (ie_opcao_p = 'V') then
		update	bl_doacao
		set	nr_atendimento = nr_atendimento_p
		where	nr_sequencia = nr_seq_doacao_p;
	elsif (ie_opcao_p = 'D') then
		update	bl_doacao
		set	nr_atendimento  = NULL
		where	nr_sequencia = nr_seq_doacao_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
