CREATE OR REPLACE PROCEDURE bsc_alterar_ano_limite_ind ( cd_empresa_p bigint, cd_estabelecimento_p bigint, nr_seq_indicador_p bigint, cd_ano_limite_p bigint, ie_gerar_informacao_p text, nm_usuario_p text) AS $body$
DECLARE


cd_ano_w	smallint;

BEGIN

select	max(cd_ano)
into STRICT	cd_ano_w
from	bsc_ind_inf
where	nr_seq_indicador	= nr_seq_indicador_p
and	cd_empresa		= cd_empresa_p
and	cd_estabelecimento	= cd_estabelecimento_p;

cd_ano_w	:= coalesce(cd_ano_w,(to_char(clock_timestamp(),'yyyy'))::numeric );

update	bsc_indicador
set	cd_ano_limite	= cd_ano_limite_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_indicador_p
and	cd_ano_limite	<> cd_ano_limite_p;

if (ie_gerar_informacao_p = 'S') then
	CALL bsc_gerar_inf_indicador(cd_empresa_p, cd_estabelecimento_p,nr_seq_indicador_p,null,cd_ano_w,nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
