CREATE OR REPLACE PROCEDURE bsc_alterar_dt_cancelamento ( nm_usuario_p text, nr_seq_ordem_p bigint, ie_opcao_p text) AS $body$
BEGIN
if (ie_opcao_p = 'N') then
	begin
	update	man_ordem_servico
	set	dt_iniciativa_cancelada  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_ordem_p;
	end;
elsif (ie_opcao_p = 'S') then
	begin
	update	man_ordem_servico
	set	dt_iniciativa_cancelada = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		ie_status_ordem = '3'
	where	nr_sequencia = nr_seq_ordem_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
