CREATE OR REPLACE PROCEDURE bsc_calcular_ctb_especial ( nr_seq_indicador_p bigint, nr_seq_ind_inf_p bigint, nr_seq_regra_calc_p bigint, qt_real_p INOUT bigint) AS $body$
DECLARE


cd_estabelecimento_W	smallint;
dt_referencia_w		timestamp;
cd_ano_w		smallint;
cd_periodo_w		smallint;
ie_origem_valor_w		varchar(15);
ds_origem_w		varchar(2000);
nr_seq_mes_ref_w		bigint;
cd_empresa_w		smallint;
vl_referencia_w		double precision;

ds_origem_conta_w	varchar(2000);
ie_origem_regra_w	varchar(15);
vl_fixo_w		double precision;
nr_seq_regra_esp_w	bigint;


BEGIN

select	cd_ano,
	cd_periodo,
	cd_empresa,
	cd_estabelecimento
into STRICT	cd_ano_w,
	cd_periodo_w,
	cd_empresa_w,
	cd_estabelecimento_w
from	bsc_ind_inf
where	nr_sequencia	= nr_seq_ind_inf_p;

dt_referencia_w	:= to_date('01/' || cd_periodo_w || '/' || cd_ano_w,'dd/mm/yyyy');

select	coalesce(max(nr_sequencia), 0)
into STRICT	nr_seq_mes_ref_w
from	ctb_mes_ref
where	cd_empresa	= cd_empresa_w
and	trunc(dt_referencia, 'mm') = trunc(dt_referencia_w, 'mm');


begin
select	ds_origem
into STRICT	ds_origem_w
from	bsc_regra_calc_ind
where	nr_sequencia		= nr_seq_regra_calc_p;
exception
	when no_data_found then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(280310);
end;
vl_referencia_w	:= bsc_calcular_regra_ctb_esp(cd_estabelecimento_w, nr_seq_mes_ref_w, nr_seq_regra_calc_p);

qt_real_p	:= coalesce(vl_referencia_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
