CREATE OR REPLACE PROCEDURE bsc_calcular_indicador ( nr_seq_indicador_p bigint, nr_seq_ind_inf_p bigint, qt_real_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_w			bigint;
nm_objeto_w			varchar(30);


ie_origem_real_w			varchar(15);
qt_real_w				double precision;

/* Regras de calculo do indicador */

c01 CURSOR FOR
SELECT	b.nr_sequencia,
	b.ie_origem_real
from	bsc_regra_calc a,
	bsc_regra_calc_ind b
where	a.nr_sequencia		= b.nr_seq_regra
and	clock_timestamp() between a.dt_inicio_validade and coalesce(a.dt_validade,clock_timestamp())
and	b.nr_seq_indicador	= nr_seq_indicador_p
order by b.nr_seq_calc;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	ie_origem_real_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_origem_real_w = 'F') then
		qt_real_w := bsc_calcular_formula_ind(nr_seq_indicador_p, nr_seq_ind_inf_p, nr_seq_regra_w, qt_real_w);
	elsif (ie_origem_real_w in ('S','P')) then
		qt_real_w := bsc_calcular_sql(nr_seq_indicador_p, nr_seq_ind_inf_p, nr_seq_regra_w, qt_real_w);
	elsif (ie_origem_real_w = 'CTB') then
		qt_real_w := bsc_calcular_ctb(nr_seq_indicador_p, nr_seq_ind_inf_p, nr_seq_regra_w, qt_real_w);
	elsif (ie_origem_real_w = 'CTBE') then
		qt_real_w := bsc_calcular_ctb_especial(nr_seq_indicador_p, nr_seq_ind_inf_p, nr_seq_regra_w, qt_real_w);
	end if;

	end;
end loop;
close C01;

qt_real_p	:= coalesce(qt_real_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
