CREATE OR REPLACE PROCEDURE bsc_gerar_fechamento_indicador ( nr_seq_indicador_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_ano_w					smallint;
cd_ano_fechamento_w				smallint;
cd_periodo_w					smallint;
dt_fechamento_w					timestamp;
dt_periodo_w					timestamp;
ie_fechado_w					varchar(1) := 'A';
ie_periodo_w					varchar(1);
nr_sequencia_w					bigint;

C01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.cd_ano,
	a.cd_periodo
from	bsc_ind_inf a
where	a.nr_seq_indicador = nr_seq_indicador_p
and	(dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '')
order by 1;

BEGIN

select	max(dt_fechamento),
	max(ie_periodo)
into STRICT	dt_fechamento_w,
	ie_periodo_w
from	bsc_indicador
where	nr_sequencia = nr_seq_indicador_p;

if (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '') then
	cd_ano_fechamento_w	:= (to_char(dt_fechamento_w,'yyyy'))::numeric;

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w,
		cd_ano_w,
		cd_periodo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_fechado_w	:= 'A';
		dt_periodo_w	:= to_date('01/' || to_char(cd_periodo_w) || '/' || to_char(cd_ano_w),'dd/mm/yyyy');

		if (ie_periodo_w = 'A') and (cd_ano_w <= cd_ano_fechamento_w) then
			ie_fechado_w	:= 'F';
		elsif (ie_periodo_w = 'M') and (dt_periodo_w <= dt_fechamento_w) then
			ie_fechado_w	:= 'F';
		elsif (ie_periodo_w = 'S') then
			begin
			if (cd_ano_w < cd_ano_fechamento_w) then
				ie_fechado_w	:= 'F';
			elsif (cd_ano_w = cd_ano_fechamento_w) and (cd_periodo_w <= (obter_periodo_data(dt_fechamento_w,'E'))::numeric ) then
				ie_fechado_w	:= 'F';
			end if;
			end;
		elsif (ie_periodo_w = 'T') then
			begin
			if (cd_ano_w < cd_ano_fechamento_w) then
				ie_fechado_w	:= 'F';
			elsif (cd_ano_w = cd_ano_fechamento_w) and (cd_periodo_w <= (obter_periodo_data(dt_fechamento_w,'T'))::numeric ) then
				ie_fechado_w	:= 'F';
			end if;
			end;
		end if;

		update	bsc_ind_inf
		set	ie_fechado	= ie_fechado_w,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_w;
		end;
	end loop;
	close C01;
else	/* Se retirar a data de fechamento, sistema coloca todos os periodos para Abertos*/
	update	bsc_ind_inf
	set	ie_fechado	= 'A',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_seq_indicador= nr_seq_indicador_p;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
