SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE bsc_gerar_inf_indicador ( cd_empresa_p bigint, cd_estabelecimento_p bigint, nr_seq_indicador_p bigint, vl_indicador_p bigint, cd_ano_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_ano_w			smallint;
cd_ano_ww			smallint;
cd_ano_limite_w			smallint;
cd_estabelecimento_w		bigint;
dt_ano_w				timestamp;
dt_ano_limite_w			timestamp;
ie_periodo_w			varchar(15);
nm_indicador_w			varchar(255);
qt_periodo_w			integer;
qt_reg_w				bigint;
mes_w				smallint;
ie_informacao_w			varchar(15);


BEGIN

select	coalesce(max(cd_ano_limite),cd_ano_p),
	max(ie_periodo),
	max(nm_indicador),
	coalesce(max(ie_informacao),'EST')
into STRICT	cd_ano_limite_w,
	ie_periodo_w,
	nm_indicador_w,
	ie_informacao_w
from	bsc_indicador
where	nr_sequencia = nr_seq_indicador_p;

cd_ano_w	:= cd_ano_p;
cd_ano_ww	:= cd_ano_p;
dt_ano_w		:= trunc(clock_timestamp(),'YYYY');
dt_ano_limite_w	:= to_date('01/12/' || to_char(cd_ano_limite_w),'dd/mm/yyyy');

if (ie_periodo_w = 'A') then
	qt_periodo_w	:= 1;
elsif (ie_periodo_w = 'M') then
	qt_periodo_w	:= 12;
elsif (ie_periodo_w = 'S') then
	qt_periodo_w	:= 2;
elsif (ie_periodo_w = 'T') then
	qt_periodo_w	:= 4;
elsif (ie_periodo_w = 'B') then
	qt_periodo_w	:= 6;
elsif (ie_periodo_w = 'Q') then
	qt_periodo_w	:= 3;
end if;

cd_estabelecimento_w	:= null;

if (ie_informacao_w = 'EST') then
	cd_estabelecimento_w	:= cd_estabelecimento_p;
end if;

for cd_ano_ww in cd_ano_w..cd_ano_limite_w loop
	begin
	for mes_w in 1..qt_periodo_w loop
		begin
		qt_reg_w	:= 0;
		if (ie_informacao_w = 'EST') then

			select	count(*)
			into STRICT	qt_reg_w
			from	bsc_ind_inf
			where	nr_seq_indicador		= nr_seq_indicador_p
			and	cd_ano				= cd_ano_ww
			and	cd_periodo			= mes_w
			and	cd_empresa			= cd_empresa_p
			and	cd_estabelecimento		= cd_estabelecimento_w;

		elsif (ie_informacao_w = 'EMP') then

			select	count(*)
			into STRICT	qt_reg_w
			from	bsc_ind_inf
			where	nr_seq_indicador		= nr_seq_indicador_p
			and	cd_ano				= cd_ano_ww
			and	cd_periodo			= mes_w
			and	cd_empresa			= cd_empresa_p;

		end if;

		if (qt_reg_w = 0) then

			insert into bsc_ind_inf(
				nr_sequencia,
				cd_empresa,
				cd_estabelecimento,
				nr_seq_indicador,
				ie_fechado,
				cd_ano,
				cd_periodo,
				qt_meta,
				qt_real,
				qt_limite,
				qt_referencia,
				nm_usuario,
				dt_atualizacao,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				ie_informado)
			values (	nextval('bsc_ind_inf_seq'),
				cd_empresa_p,
				cd_estabelecimento_w,
				nr_seq_indicador_p,
				'A',
				cd_ano_ww,
				mes_w,
				coalesce(vl_indicador_p,0),
				0,
				0,
				0,
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				'N');

		end if;
		end;
	end loop;
	end;
end loop;
commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
