CREATE OR REPLACE PROCEDURE bsc_liberar_indic_dia (cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_ind_inf_w	bigint;
dt_referencia_w		timestamp;
nr_dia_liberacao_w	bigint;


c01 CURSOR FOR
SELECT	b.nr_sequencia
from	bsc_indicador a,
	bsc_ind_inf b
where	a.nr_sequencia	= b.nr_seq_indicador
and	(a.nr_dia_liberacao IS NOT NULL AND a.nr_dia_liberacao::text <> '')
and	a.nr_dia_liberacao < nr_dia_liberacao_w
and	coalesce(b.dt_liberacao::text, '') = ''
and	bsc_obter_data_inf(b.nr_sequencia) = dt_referencia_w
order by 1;


BEGIN

dt_referencia_w		:= PKG_DATE_UTILS.start_of(PKG_DATE_UTILS.ADD_MONTH(PKG_DATE_UTILS.start_of(clock_timestamp(),'month',0),-1,0),'month',0);
nr_dia_liberacao_w	:= PKG_DATE_UTILS.extract_field('DAY', clock_timestamp());

open c01;
loop
fetch c01 into
	nr_seq_ind_inf_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	CALL bsc_liberar_inf_indicador(nr_seq_ind_inf_w, cd_estabelecimento_p, 'L', nm_usuario_p);

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
