SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE busca_alert_anest_diagn ( ds_lista_p text, ds_lista_cod_p INOUT text, ds_lista_diagn_p INOUT text) AS $body$
DECLARE


c_diagnostico CURSOR FOR
SELECT 	a.nr_sequencia,
		a.ds_diagnostico
from 	apa_diagnostico a
where 	coalesce(a.ie_utiliza_diag_alerta, 'N') = 'S'
and 	a.nr_sequencia in (WITH RECURSIVE cte AS (
select regexp_substr(ds_lista_p,'[^,]+', 1, level)
                        
                        (regexp_substr(ds_lista_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(ds_lista_p, '[^,]+', 1, level))::text <> '')  UNION ALL
select regexp_substr(ds_lista_p,'[^,]+', 1, level) JOIN cte c ON ()

) SELECT * FROM cte;
);


BEGIN
if (ds_lista_p IS NOT NULL AND ds_lista_p::text <> '') then
	<<montar_lista>>
   for r_diagnostico in c_diagnostico
   loop
      ds_lista_cod_p := ltrim(ds_lista_cod_p || ',' || r_diagnostico.nr_sequencia,',');
	  ds_lista_diagn_p  := ltrim(ds_lista_diagn_p || ' , ' || r_diagnostico.ds_diagnostico,', ');
   end loop montar_lista;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
