SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cadastra_novo_soft_capturado (nr_seq_captura_w bigint,nm_usuario_p text) AS $body$
DECLARE


ds_software_cap_w	 varchar(255);
nr_id_soft_cap_w	 bigint;

C01 CURSOR FOR
	SELECT	distinct a.nr_id_soft_cap,
			 a.ds_software_cap
	from	captura_software_detalhe a
	where	a.nr_seq_capt_software = nr_seq_captura_w
	and	not exists (	SELECT 	1
				from	software b
				where	a.nr_id_soft_cap = b.nr_id_soft_cap);

BEGIN



open C01;
loop
fetch C01 into
	nr_id_soft_cap_w,
	ds_software_cap_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		insert into software(
			nr_sequencia,
			nm_software,
			ds_software,
			nr_id_soft_cap,
			ie_situacao,
			ie_tipo_licenca,
			ie_instalacao_restrita,
			ie_status,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec
		) values (
			nextval('software_seq'),
			substr(ds_software_cap_w,1,60),
			ds_software_cap_w,
			nr_id_soft_cap_w,
			'A',
			'B',
			'S',
			'P',
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp()
		);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
