CREATE OR REPLACE PROCEDURE cadastra_nut_item_extra ( dt_servico_p timestamp, nm_usuario_p text, nr_atendimento_p bigint, nr_seq_servico_p bigint, nr_seq_gen_alim_p bigint, ds_observacao_p text, nr_setor_atendimento_p bigint ) AS $body$
DECLARE


nr_sequencia_w bigint;
count_w bigint;


BEGIN
	select count(nr_sequencia),
	max(nr_sequencia)
	into STRICT count_w,
	nr_sequencia_w
	from NUT_ATEND_SERV_DIA
	where trunc(dt_servico) = trunc(dt_servico_p) 
	and nr_atendimento = nr_atendimento_p
	and nr_seq_servico = nr_seq_servico_p;

	if (count_w > 0) then

		BEGIN
			INSERT INTO NUT_ITEM_EXTRA(
			NR_SEQ_SERVICO,
			IE_TIPO_REQUISICAO,
			IE_TIPO_PESSOA,
			IE_TIPO_ITEM_EXTRA,
			NR_SEQUENCIA,
			DT_ATUALIZACAO,
			NM_USUARIO,
			DT_ATUALIZACAO_NREC,
			NM_USUARIO_NREC,
			NR_SEQ_GEN_ALIM,
			DS_OBSERVACAO
			) VALUES (
			nr_sequencia_w,
			'A',
			'P',
			'T',
			(nextval('nut_item_extra_seq')),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_gen_alim_p,
			ds_observacao_p
			);
		end;
	else

		begin
			insert into NUT_ATEND_SERV_DIA(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_servico,
			dt_servico,
			cd_setor_atendimento,
			nr_atendimento)

			values (
			(nextval('nut_atend_serv_dia_seq')),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_servico_p,
			clock_timestamp(),
			nr_setor_atendimento_p,
			nr_atendimento_p);

			insert into NUT_ITEM_EXTRA(
			NR_SEQ_SERVICO,
			IE_TIPO_REQUISICAO,
			IE_TIPO_PESSOA,
			IE_TIPO_ITEM_EXTRA,
			NR_SEQUENCIA,
			DT_ATUALIZACAO,
			NM_USUARIO,
			DT_ATUALIZACAO_NREC,
			NM_USUARIO_NREC,
			NR_SEQ_GEN_ALIM,
			DS_OBSERVACAO
			) VALUES (
			(SELECT max(nr_sequencia) from NUT_ATEND_SERV_DIA),
			'A',
			'P',
			'T',
			(nextval('nut_item_extra_seq')),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_gen_alim_p,
			ds_observacao_p
			);
		end;
	end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
