CREATE OR REPLACE PROCEDURE caixa_saldo_auditoria ( nr_seq_caixa_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE


nr_regra_w	caixa_regra_auditoria.nr_sequencia%type;
ie_movimento_saldo_w caixa_regra_auditoria.ie_movimento_saldo%type;
dt_auditoria_w   timestamp;


BEGIN

nr_regra_w := obter_regra_auditoria_caixa(nr_seq_caixa_p);

if (nr_regra_w IS NOT NULL AND nr_regra_w::text <> '') then
	
	select max(ie_movimento_saldo)
	into STRICT ie_movimento_saldo_w
	from caixa_regra_auditoria
	where nr_sequencia = nr_regra_w;
	
	select max(dt_auditoria)
	into STRICT dt_auditoria_w
	from caixa_saldo_diario
	where nr_sequencia = nr_sequencia_p;
	
	if (ie_movimento_saldo_w = 'N' and (dt_auditoria_w IS NOT NULL AND dt_auditoria_w::text <> '')) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1134174);
	end if;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
