CREATE OR REPLACE PROCEDURE calcular_caboplanina_loco_reg ( cd_pessoa_fisica_p text, qt_peso_p bigint, qt_creatinina_p bigint, ie_unid_med_p bigint, auc_p bigint, qt_clearance_p INOUT bigint, qt_carboplatina_p INOUT bigint, qt_redutor_clcr_p text default 0) AS $body$
DECLARE



dt_nascimento_w		timestamp;
qt_idade_w		smallint;
ie_sexo_w		varchar(1);
qt_altura_w		real;
qt_clearance_w	double precision;


BEGIN
qt_carboplatina_p	:= 0;

select	max(dt_nascimento),
	MAX(ie_sexo)
into STRICT	dt_nascimento_w,
	ie_sexo_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

select	(obter_idade(dt_nascimento_w,clock_timestamp(),'A'))::numeric
into STRICT	qt_idade_w
;

if (qt_peso_p > 0) and (qt_creatinina_p > 0) then
	begin
	qt_clearance_p	:= dividir(((140 - qt_idade_w) * qt_peso_p) , (7.2 * qt_creatinina_p));

	if (coalesce(ie_unid_med_p,1) = 1) then
		qt_clearance_p	:= qt_clearance_p * 0.10;
	end if;

	if (ie_sexo_w = 'F') then
		qt_clearance_p	:= qt_clearance_p * 0.85;
	end if;

	end;
end if;

qt_clearance_w	:= qt_clearance_p;

if (QT_REDUTOR_CLCR_p IS NOT NULL AND QT_REDUTOR_CLCR_p::text <> '') and (QT_REDUTOR_CLCR_p	<> 0) then

	qt_clearance_w	:= qt_clearance_w - dividir((qt_clearance_p * QT_REDUTOR_CLCR_p),100);
end if;



if (qt_clearance_p IS NOT NULL AND qt_clearance_p::text <> '') then
	qt_carboplatina_p	:= (auc_p * (qt_clearance_w + 25));
end if;


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
