CREATE OR REPLACE PROCEDURE calcular_capac_centro_controle ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, cd_centro_controle_p bigint, nm_usuario_p text, nr_seq_tabela_p bigint) AS $body$
DECLARE


dt_atualizacao_w				timestamp		:= clock_timestamp();
cd_centro_controle_w				integer	:= 0;
nr_sequencia_nivel_w				bigint	:= 0;
qt_disponibilidade_w				double precision	:= 0;
qt_disp_calculada_w				double precision	:= 0;
qt_reducao_w					double precision	:= 0;
ie_tipo_redutor_w				varchar(02);

c01 CURSOR FOR
SELECT	nr_sequencia_nivel,
	qt_disponibilidade
from	capac_centro_controle
where	nr_seq_tabela		= nr_seq_tabela_p
and	cd_centro_controle	= cd_centro_controle_p
and	cd_estabelecimento	= cd_estabelecimento_p
order by nr_sequencia_nivel;

c02 CURSOR FOR
SELECT a.qt_reducao,
	b.ie_tipo_redutor
from	reduc_capac_centro_controle a,
	redutor_capacidade b
where	a.cd_redutor_capacidade = b.cd_redutor_capacidade
and	a.nr_sequencia_nivel    = nr_sequencia_nivel_w
and	a.nr_seq_tabela		= nr_seq_tabela_p
and	a.cd_centro_controle    = cd_centro_controle_p;

BEGIN


open c01;
loop
fetch c01 into
	nr_sequencia_nivel_w,
	qt_disponibilidade_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (nr_sequencia_nivel_w = 1) then
		qt_disp_calculada_w   := qt_disponibilidade_w;
	else
		qt_disponibilidade_w  := qt_disp_calculada_w;
	end if;
	open c02;
	loop
	fetch c02 into
		qt_reducao_w,
		ie_tipo_redutor_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		if (ie_tipo_redutor_w = '2') then
			qt_disp_calculada_w := qt_disp_calculada_w - qt_reducao_w;
		else
			qt_disp_calculada_w := qt_disp_calculada_w - (qt_disponibilidade_w * qt_reducao_w / 100);
		end if;
		end;
       end loop;
       close c02;


	update		capac_centro_controle
	set		qt_disponibilidade	= qt_disp_calculada_w
	where		nr_seq_tabela		= nr_seq_tabela_p
	and		cd_centro_controle	= cd_centro_controle_p
	and		nr_sequencia_nivel	= nr_sequencia_nivel_w
	and		cd_estabelecimento 	= cd_estabelecimento_p;
	end;
end loop;
close c01;



commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
